package javaArrayexercise;

import java.util.Arrays;

public class numbaricStringArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int []arry1 = { 1,2,3,4,5 };
	String []arry2 = { "Raya", "Flavia", "Topu", "Terence"};
	
	// Print the original numeric array.
    System.out.println("Original numeric array : " + Arrays.toString(arry1));
	
 // Sort the numeric array .
    System.out.println ( " Sort numbaric arrays :"+ Arrays.toString (arry1));
	
 // Print the original string array.
    System.out.println("Original string array : " + Arrays.toString(arry2));
	
	//Sort the string array.
    System.out.println(" Sort string array : " + Arrays.toString (arry2));
	}

}
