package localandinstancevariable;

public class LocalVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		localvar();
		localvar1();
	}

	
	public static void localvar() {
		
		int a = 10;
	System.out.println(a);
	}
	public static void localvar1() {
		
		int a = 20;
	System.out.println(a);
		
	}			
	
}
